package aquadew.database;import aquadew.core.ADException;import snowblozm.interfaces.Block;import snowblozm.interfaces.Sequence;/** *	@interface DataService *	@desc Abstract interface for database services  * *	@author Vibhaj Rajan <vibhaj8@gmail.com> ***/public interface DataService {	/** 	 *	@method open	 *	@desc open connection to data service	 *	 *	@param url string Database JDBC URL	 *	@param user string Database user	 *	@param pass string Datanse password	 *	**/	public void open(String url, String user, String pass) throws ADException;		/** 	 *	@method getResult	 *	@desc executes query and returns resultset if execute is false, else return affected row count	 *	 *	@param query string SQL query	 *	@param values sequence Array containing mappings between types and values	 *	@param type integer Execute type (0=Select 1=Update/Delete 2=Insert)	 *	 *	@value 1 string	 *	@value 2 integer	 *	@value 3 long	 *	@value 4 double	 *	@value 5 boolean	 *	@value 6 date	 *	@value 7 time	 *	@value 8 timestamp	 *	 *	@return result array/integer/false	 *	**/	public Object getResult(String query, Sequence values, int type) throws ADException;		/** 	 *	@method getAutoId	 *	@desc gets the last auto-increment id	 *	 *	@return $result long ID	 *	**/	public long getAutoId() throws ADException;		/** 	 *	@method close	 *	@desc closes the connection	 *	**/	public void close() throws ADException;	}